import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    navigate('/login');
    setIsMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className={`navbar ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
      <div className="navbar-left">
        <Link to="/" className="logo">Nitec.</Link>
        <div className="search-bar">
          <input 
            type="text" 
            placeholder="Search products..." 
            className="search-input"
          />
          <button className="search-button">
            <i className="fas fa-search"></i>
          </button>
        </div>
      </div>

      <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
        <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
      </button>

      <div className="navbar-right">
        <Link to="/cart" className="nav-icon" onClick={() => setIsMobileMenuOpen(false)}>
          <i className="fas fa-shopping-bag"></i>
          <span>Cart</span>
        </Link>
        <Link to="/favorites" className="nav-icon" onClick={() => setIsMobileMenuOpen(false)}>
          <i className="fas fa-heart"></i>
          <span>Favorites</span>
        </Link>
        {username ? (
          <div className="user-profile">
            <span>{username}</span>
            <button onClick={handleLogout} className="logout-button">
              <i className="fas fa-sign-out-alt"></i>
            </button>
          </div>
        ) : (
          <Link 
            to="/login" 
            className="login-button"
            onClick={() => setIsMobileMenuOpen(false)}
          >
            Login
          </Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
