import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getItem, addToCart } from '../services/api';
import './ProductDetail.css';

function ProductDetail() {
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    loadProduct();
  }, [id]);

  const loadProduct = async () => {
    try {
      setLoading(true);
      const data = await getItem(id);
      setProduct(data);
    } catch (error) {
      console.error('Error loading product:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddToCart = async () => {
    const isAuthenticated = localStorage.getItem('token');

    if (!isAuthenticated) {
      localStorage.setItem('redirectPath', window.location.pathname);
      navigate('/login');
      return;
    }

    try {
      await addToCart(product.id);
      alert('Added to cart successfully!');
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="error-container">
        <h2>Product Not Found</h2>
        <button onClick={() => navigate('/')}>Return to Home</button>
      </div>
    );
  }

  return (
    <div className="product-detail-container">
      <div className="product-detail-content">
        <div className="product-images">
          <div className="main-image">
            <img 
              src={product.image_urls} 
              alt={product.name} 
              className="primary-image"
            />
          </div>
        </div>

        <div className="product-info">
          <h1 className="product-title">{product.name}</h1>
          <div className="product-brand">
            BY <span className="brand-name">{product.brand}</span>
          </div>

          <div className="product-price">
            <div className="price-tag">
              <span className="currency">$</span>
              <span className="amount">{product.price}</span>
            </div>
            <div className="price-info">
              <span className="discount">Save 12%</span>
              <span className="tax-info">Inclusive of all Taxes</span>
            </div>
          </div>

          <div className="product-description">
            <p>{product.description}</p>
          </div>

          <div className="purchase-options">
            <div className="quantity-selector">
              <label>QTY</label>
              <select 
                value={quantity} 
                onChange={(e) => setQuantity(Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5].map(num => (
                  <option key={num} value={num}>{num}</option>
                ))}
              </select>
            </div>
            <button 
              className="add-to-cart-btn"
              onClick={handleAddToCart}
            >
              Add to Cart
            </button>
          </div>

          <div className="reviews">
            <div className="reviewer">
              <img 
                src="/images/reviewer-avatar.png" 
                alt="Reviewer" 
                className="reviewer-avatar"
              />
              <div className="reviewer-info">
                <h3>Alex Parkinson</h3>
                <p>Good Product for Daily Use</p>
                <p className="review-text">
                  {product.description}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
