import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getItems, addToCart } from '../services/api';
import './ProductDetail.css';

function ProductDetail() {
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    loadProduct();
  }, [id]);

  const loadProduct = async () => {
    try {
      const data = await getItems();
      const selectedProduct = data.find(item => item.id === parseInt(id));
      if (selectedProduct) {
        setProduct(selectedProduct);
      }
    } catch (error) {
      console.error('Error loading product:', error);
    }
  };

  const handleAddToCart = async () => {
    const isAuthenticated = localStorage.getItem('token');

    if (!isAuthenticated) {
      localStorage.setItem('redirectPath', window.location.pathname);
      navigate('/login');
      return;
    }

    try {
      await addToCart(product.id);
      alert('Added to cart successfully!');
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  if (!product) {
    return <div className="loading">Loading...</div>;
  }

  // Mock images array (you would replace these with actual product images)
  const productImages = [
    `/images/${product.name.toLowerCase().replace(/\s+/g, '-')}.png`,
    `/images/${product.name.toLowerCase().replace(/\s+/g, '-')}-2.png`,
    `/images/${product.name.toLowerCase().replace(/\s+/g, '-')}-3.png`,
  ];

  return (
    <div className="product-detail-container">
      <div className="breadcrumb">
        <span onClick={() => navigate('/')}>HOME</span>
        <span className="separator">&gt;</span>
        <span onClick={() => navigate('/category')}>CATEGORY</span>
        <span className="separator">&gt;</span>
        <span>{product.name}</span>
      </div>

      <div className="product-detail-content">
        <div className="product-images">
          <div className="main-image">
            <img 
              src={productImages[selectedImage]} 
              alt={product.name} 
              className="primary-image"
            />
          </div>
          <div className="thumbnail-images">
            {productImages.map((img, index) => (
              <div 
                key={index}
                className={`thumbnail ${selectedImage === index ? 'selected' : ''}`}
                onClick={() => setSelectedImage(index)}
              >
                <img src={img} alt={`${product.name} view ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>

        <div className="product-info">
          <h1 className="product-title">{product.name}</h1>
          <div className="product-brand">
            BY <span className="brand-name">KC COMPANY</span>
          </div>

          <div className="product-price">
            <div className="price-tag">
              <span className="currency">$</span>
              <span className="amount">25</span>
            </div>
            <div className="price-info">
              <span className="discount">Save 12%</span>
              <span className="tax-info">Inclusive of all Taxes</span>
            </div>
          </div>

          <div className="product-description">
            <p>{product.description || 'Capable of mincing, peeling & cutting, these knives are for both professional and home use.'}</p>
          </div>

          <div className="purchase-options">
            <div className="quantity-selector">
              <label>QTY</label>
              <select 
                value={quantity} 
                onChange={(e) => setQuantity(Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5].map(num => (
                  <option key={num} value={num}>{num}</option>
                ))}
              </select>
            </div>
            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Add to Cart
            </button>
          </div>

          <div className="reviews">
            <div className="reviewer">
              <img 
                src="/images/reviewer-avatar.png" 
                alt="Reviewer" 
                className="reviewer-avatar"
              />
              <div className="reviewer-info">
                <h3>Alex Parkinson</h3>
                <p>Good Blade for Precise Cutting</p>
                <p className="review-text">
                  Capable of mincing, peeling & cutting, these knives are perfect for both professional and home use.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
